import React, { createContext, useState, useContext, useEffect } from 'react';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  // Load user from localStorage on mount
  useEffect(() => {
    const savedUser = localStorage.getItem('user');
    if (savedUser) {
      const userData = JSON.parse(savedUser);
      setUser(userData);
      setIsAuthenticated(true);
    }
  }, []);

  const login = (username, password, role) => {
    // Simple authentication logic - in real app, this would call an API
    const users = JSON.parse(localStorage.getItem('users') || '[]');

    if (role === 'admin') {
      // Admin credentials
      if (username === 'admin' && password === 'admin123') {
        const adminUser = { username, role: 'admin', id: 'admin' };
        setUser(adminUser);
        setIsAuthenticated(true);
        localStorage.setItem('user', JSON.stringify(adminUser));
        return true;
      }
    } else {
      // Client login - check against stored users
      const clientUser = users.find(u => u.username === username && u.password === password);
      if (clientUser) {
        const userData = { username: clientUser.username, role: 'client', id: clientUser.id };
        setUser(userData);
        setIsAuthenticated(true);
        localStorage.setItem('user', JSON.stringify(userData));
        return true;
      }
    }
    return false;
  };

  const logout = () => {
    setUser(null);
    setIsAuthenticated(false);
    localStorage.removeItem('user');
  };

  const createClient = (username, password) => {
    const users = JSON.parse(localStorage.getItem('users') || '[]');
    const newUser = {
      id: Date.now().toString(),
      username,
      password,
      role: 'client',
      createdAt: new Date().toISOString()
    };
    users.push(newUser);
    localStorage.setItem('users', JSON.stringify(users));
    return newUser;
  };

  const getClients = () => {
    return JSON.parse(localStorage.getItem('users') || '[]');
  };

  const deleteClient = (userId) => {
    const users = JSON.parse(localStorage.getItem('users') || '[]');
    const filteredUsers = users.filter(u => u.id !== userId);
    localStorage.setItem('users', JSON.stringify(filteredUsers));
  };

  return (
    <AuthContext.Provider value={{
      user,
      isAuthenticated,
      login,
      logout,
      createClient,
      getClients,
      deleteClient
    }}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => useContext(AuthContext);
